    private void SimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SimpanActionPerformed
    String hasil = "Ini adalah hasil yang ingin disimpan ke file .txt";
    String namaFile = "hasil.txt";

    try {
        FileWriter writer = new FileWriter(namaFile);
        writer.write(hasil);
        writer.close();
        System.out.println("Hasil berhasil disimpan ke " + namaFile);
    } catch (IOException e) {
        e.printStackTrace();
    }


    }//GEN-LAST:event_SimpanActionPerformed

