    private void HitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_HitungActionPerformed
     int tahun = Integer.parseInt(Tahun.getText());
    String bulanDipilih = Bulan.getSelectedItem().toString();
    int jumlahHari;

    if (bulanDipilih.equalsIgnoreCase("Januari") || bulanDipilih.equalsIgnoreCase("Maret") ||
        bulanDipilih.equalsIgnoreCase("Mei") || bulanDipilih.equalsIgnoreCase("Juli") ||
        bulanDipilih.equalsIgnoreCase("Agustus") || bulanDipilih.equalsIgnoreCase("Oktober") ||
        bulanDipilih.equalsIgnoreCase("Desember")) {
        jumlahHari = 31;
    } else if (bulanDipilih.equalsIgnoreCase("April") || bulanDipilih.equalsIgnoreCase("Juni") ||
               bulanDipilih.equalsIgnoreCase("September") || bulanDipilih.equalsIgnoreCase("November")) {
        jumlahHari = 30;
    } else if (bulanDipilih.equalsIgnoreCase("Februari")) {
        jumlahHari = 28;
    } else {
        Hasil.setText("Bulan tidak valid.");
        return;
    }

    Hasil.setText("Jumlah hari pada bulan " + bulanDipilih + " tahun " + tahun + " adalah " + jumlahHari);

    }//GEN-LAST:event_HitungActionPerformed

